from rest_framework.pagination import PageNumberPaginationfrom rest_framework.response import Responseclass OrderPagination(PageNumberPagination):    page_size_query_param = 'page_size'    page_query_param = "page"    max_page_size = 10000    # 自定义    def get_paginated_response(self, data):        return Response({            'count': self.page.paginator.count,            'results': data        })